from Crypto.Util.number import *
import random

primes = []

for i in range(1000):
    primes.append(getPrime(64))

def getMyPrime(nbits: int):
    while True:
        n = 2
        while n.bit_length() < nbits:
            n *= random.choice(primes)

        if isPrime(n-1):
            return n-1

p = getMyPrime(512)
q = getPrime(512)
n = p*q
e = 65537
flag = b'NSSCTF{******}'
c = pow(bytes_to_long(flag), e, n)

print(f'n = {n}')
print(f'c = {c}')
